import React from "react";
import { ThemeButton } from "../../../styles/GlobalComponents";

export const EventButton = ({ children, loading, onClick, buttonStyle, buttonSize }) => {
  return (
    <>
      {loading ? (
        <ThemeButton disabled>Carregando...</ThemeButton>
      ) : (
        <ThemeButton buttonStyle={buttonSize} buttonSize={buttonStyle} onClick={onClick}>{children}</ThemeButton>
      )}
    </>
  );
};

export const SubmitButton = ({ children, loading, buttonStyle, buttonSize }) => {
  return (
    <>
      {loading ? (
        <ThemeButton disabled>Carregando...</ThemeButton>
      ) : (
        <ThemeButton type="submit" buttonStyle={buttonSize} buttonSize={buttonStyle}>{children}</ThemeButton>
      )}
    </>
    )
};

export const LinkButton = ({ children, buttonStyle, buttonSize }) => {
  return <ThemeButton buttonStyle={buttonSize} buttonSize={buttonStyle}>{children}</ThemeButton>;
};
